
// make a sprite sheet
var mkCanvas = function (game) {

    // sprite sheet generated by canvas
    var canvas = document.createElement('canvas'),
    ctx = canvas.getContext('2d');
    canvas.width = 32;
    canvas.height = 32;

    // blue frame
    ctx.fillStyle = '#0000ff';
    ctx.fillRect(0, 0, 32, 32);

    game.cache.addSpriteSheet('sheet-block', null, canvas, 32, 32, 2, 0, 0);

};

var game = new Phaser.Game(320, 240, Phaser.AUTO, 'gamearea');

game.state.add('demo', {

    create: function () {

        mkCanvas(game);

        var sprite = game.add.sprite(0, 0, 'sheet-block');

        var renTex = game.add.renderTexture(32, 32, 'box');

        renTex.render(sprite);

        var sprite2 = game.add.sprite(64, 64, renTex);

    }

});

game.state.start('demo');
