

// make a sprite sheet
var mkSheet = function (game) {

    // sprite sheet generated by canvas
    var canvas = document.createElement('canvas'),
    ctx = canvas.getContext('2d');
    canvas.width = 96;
    canvas.height = 32;

    // blue box (player)
    ctx.fillStyle = '#0000ff';
    ctx.fillRect(0, 0, 32, 32);

    game.cache.addSpriteSheet('__default', null, canvas, 32, 32, 0, 0, 0);

};

var game = new Phaser.Game(320, 240, Phaser.AUTO, 'gamearea');

game.state.add('components', {

    create: function () {

        game.data = {};

        // make a simple sheet
        mkSheet(game);

        var sprite = game.add.sprite(0, 0),
        textStyle = {
            fill: 'white',
            font: '10px courier'
        };

        Object.keys(sprite.components).forEach(function (componentName, i) {

            var text = game.add.text(0, i * 10, componentName, textStyle);

        });

    }

});

game.state.start('components');
