// make a sprite sheet
// make a sprite sheet
var mkSheet = function (game) {

    // sprite sheet generated by canvas
    var canvas = document.createElement('canvas'),
    ctx = canvas.getContext('2d');
    canvas.width = 96;
    canvas.height = 32;

    // red box
    ctx.fillStyle = '#ff0000';
    ctx.fillRect(0, 0, 31, 31);

    // triangle
    ctx.beginPath();
    ctx.moveTo(34, 8);
    ctx.lineTo(62, 16);
    ctx.lineTo(34, 24);
    ctx.closePath();
    ctx.fill();

    game.cache.addSpriteSheet('sheet', null, canvas, 32, 32, 2, 0, 0);

};

var game = new Phaser.Game(320, 240, Phaser.AUTO, 'gamearea');

game.state.add('demo', {

    create: function () {

        game.data = game.data || {};

        var data = game.data;

        mkSheet(game);

        var ship = data.ship = game.add.sprite(32, 32, 'sheet', 1);
        ship.data.targetRadian = Math.PI;
        ship.anchor.set(0.5, 0.5);

    },

    update: function () {

        var data = game.data,
        cursors = data.cursors,
        ship = data.ship;

        ship.rotation = Phaser.Math.rotateToAngle(ship.rotation, ship.data.targetRadian, 0.05);

    }

});

game.state.start('demo')
