var game = new Phaser.Game(320, 240, Phaser.AUTO, 'gamearea');

// make a sprite sheet
var mkSheet = function (game) {

    // sprite sheet generated by canvas
    var canvas = document.createElement('canvas'),
    ctx = canvas.getContext('2d');
    canvas.width = 64;
    canvas.height = 32;

    // blue frame
    ctx.fillStyle = '#0000ff';
    ctx.fillRect(0, 0, 32, 32);

    // red frame
    ctx.fillStyle = '#ff0000';
    ctx.fillRect(32, 0, 32, 32);

    game.cache.addSpriteSheet('sheet-block', null, canvas, 32, 32, 2, 0, 0);

};

game.state.add('mask-demo', {

    create: function () {

        var texture = game.make.graphics();
        texture.beginFill(0xff0000);
        texture.drawRect(0, 0, 320, 240);
        texture.endFill();

        var mask = game.make.graphics(0, 0);
        mask.beginFill(0xffffff);
        //mask.drawRect(0, 0, 16, 16);
        mask.drawCircle(16, 16, 16);
        //mask.endFill();

        //Here we add a Sprite to the display list
        sprite = game.add.sprite(0, 0, texture.generateTexture());
        sprite.scale.set(2);

        //A mask is a Graphics object
        mask = game.add.graphics(0, 0);

        //Shapes drawn to the Graphics object must be filled.
        mask.beginFill(0xffffff);

        //Here we'll draw a circle
        mask.drawCircle(16, 16, 16);

        //And apply it to the Sprite
        sprite.mask = mask;

    }

});

game.state.start('mask-demo');
