// the main game variable
var game = new Phaser.Game(320, 240, Phaser.AUTO, 'gamearea');

game.global = {

    block_pool_size: 12

};

game.state.add('boot', {

    create: function () {

        // sprite sheet generated by canvas
        var canvas = document.createElement('canvas'),
        ctx = canvas.getContext('2d');
        canvas.width = 32;
        canvas.height = 32;

        ctx.fillStyle = '#0000ff';
        ctx.fillRect(0, 0, canvas.width, canvas.height);

        game.cache.addSpriteSheet('sheet-block', null, canvas, 32, 32, 1, 0, 0);

        game.state.start('game');

    }

});

game.state.add('game', {

    create: function () {

        var i = 0,
        len = game.global.block_pool_size,
        sprite;
        while (i < len) {

            sprite = game.add.sprite(-32, -32, 'sheet-block', 0);
            sprite.name = 'block-' + i;

            sprite.data = {

                active: false,
                hp: 1

            };

            i += 1;

        }

    },

    update: function () {

        var i = 0,
        len = game.global.block_pool_size,
        a,
        sprite;
        while (i < len) {

            sprite = game.world.getByName('block-' + i);

            if (!sprite.data.active) {

                sprite.data.active = true;

                a = Math.PI * 2 * Math.random();

                sprite.x = Math.cos(a) * (game.world.width / 2) + game.world.centerX - sprite.width / 2;
                sprite.y = Math.sin(a) * (game.world.height / 2) + game.world.centerY - sprite.height / 2;

            }

            i += 1;
        }

    }

});

game.state.start('boot');
